import React from 'react';
import CommonSection from '../shared/CommonSection';
import Newsletter from '../shared/Newsletter';
import { Container, Row, Col } from 'reactstrap';

import '../styles/about.css';

const About = () => {
  return (
    <>
      <CommonSection title="Про нас" />

      <section className="about-section">
        <Container>
          <Row>
            <Col lg="6" md="6">
              <div className="about-content">
                <h2>Ласкаво просимо до нашої туристичної компанії</h2>
                <p>
                  Ми — команда професіоналів, яка вже понад 10 років організовує неймовірні подорожі по всьому світу.
                  Наша мета — зробити ваш відпочинок незабутнім, безпечним та комфортним. Ми пропонуємо тури на будь-який смак: від екстремальних пригод до спокійного пляжного релаксу.
                </p>
                <p>
                  У нас працюють досвідчені менеджери, які допоможуть вам підібрати найкращий варіант, враховуючи всі ваші побажання.
                  Ми співпрацюємо лише з перевіреними партнерами, щоб гарантувати якість послуг.
                </p>
              </div>
            </Col>

            <Col lg="6" md="6">
              <div className="about-img">
                <img src="/images/about-us.jpg" alt="Про нас" className="w-100 rounded-3" />
              </div>
            </Col>
          </Row>
        </Container>
      </section>

      <Newsletter />
    </>
  );
};

export default About;
