import React from 'react';
import { Container, Row, Col } from 'reactstrap';
import Subtitle from '../shared/Subtitle';
import '../styles/about.css'; 

const About = () => {
  return (
    <section className="about__section">
      <Container>
        <Row>
          <Col lg='6' md='6'>
            <div className="about__content">
              <Subtitle subtitle="Про нас" />
              <h2 className="about__title">Ми відкриваємо світ для кожного</h2>
              <p>
                Наша туристична компанія — це команда професіоналів, яка вже багато років допомагає мандрівникам знаходити найкращі маршрути, тури та враження. 
                Ми прагнемо зробити кожну подорож унікальною і незабутньою.
              </p>
              <p>
                Незалежно від того, чи ви шукаєте розкішний відпочинок, пригодницький тур або культурне занурення — ми знайдемо ідеальний варіант саме для вас. 
                Довірте свою подорож нам — і ви отримаєте не лише якісний сервіс, але й справжнє натхнення.
              </p>
            </div>
          </Col>

          <Col lg='6' md='6'>
            <div className="about__img">
              <img src={aboutImg} alt="Про нас" className="w-100 rounded-3" />
            </div>
          </Col>
        </Row>
      </Container>
    </section>
  );
};

export default About;
